/*********************************************************************
*
*       _cbDialog
*/
static void _cbDialog(WM_MESSAGE * pMsg) {
  WM_HWIN hItem;
  int     NCode;
  int     Id;
  switch (pMsg->MsgId) {
	case WM_PAINT:
		MWin_PaintDialog(pMsg);
  case WM_INIT_DIALOG:
    //
    // Initialization of 'Main_Window'
    //
    hItem = pMsg->hWin;
//    WINDOW_SetBkColor(hItem, GUI_MAKE_COLOR(0x00C53858));
    //
    // Initialization of 'Record'
    //
    hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_0);
    BUTTON_SetFont(hItem, GUI_FONT_16B_ASCII);
    BUTTON_SetText(hItem, "Record");
    //
    // Initialization of 'PAGE DN'
    //
//    hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_1);
//    BUTTON_SetText(hItem, "PAGE DN");
//    BUTTON_SetFont(hItem, GUI_FONT_16B_ASCII);
    //
    // Initialization of 'Ctrl Panel'
    //
    hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_2);
    BUTTON_SetText(hItem, "Ctrl Panel");
    BUTTON_SetFont(hItem, GUI_FONT_16B_ASCII);
    break;
  case WM_NOTIFY_PARENT:
    Id    = WM_GetId(pMsg->hWinSrc);
    NCode = pMsg->Data.v;
    switch(Id) {
    case ID_BUTTON_0: // Notifications sent by 'PAGE UP'
      switch(NCode) {
      case WM_NOTIFICATION_CLICKED:
        break;
      case WM_NOTIFICATION_RELEASED:
				xSemaphoreGive(xMaWinFlag_OFF);
				xSemaphoreGive(xRecWinFlag_ON);
				vTaskResume(Record_WinHandle);
        break;
      }
      break;

    case ID_BUTTON_2: // Notifications sent by 'Ctrl Panel'
      switch(NCode) {
      case WM_NOTIFICATION_CLICKED:
        break;
      case WM_NOTIFICATION_RELEASED:
				xSemaphoreGive(xMaWinFlag_OFF);
				xSemaphoreGive(xCtrlWinFlag_ON);
				vTaskResume(Control_WinHandle);
        break;
      }
      break;
    }
    break;
  default:
    WM_DefaultProc(pMsg);
    break;
  }
}
