/*********************************************************************
*
*       CreateMain_Window
*/
void CreateMain_Window(void *pvParameters);
void CreateMain_Window(void *pvParameters) {
  WM_HWIN hWin;
	uint8_t MWin_Flag=1;
	
	WM_SetCreateFlags(WM_CF_MEMDEV);  						/* Use memory devices on all windows to avoid flicker */
	WM_SetDesktopColor(GUI_BLACK);      					/* Automacally update desktop window */
  hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
	while(1)
	{	
		if(xSemaphoreTake(xMaWinFlag_OFF,0) == pdPASS)
		{
			if(MWin_Flag ==1)
			{
				MWin_Flag = 0;
				Init_TMsg(&MT_Data);
				GUI_EndDialog(WM_GetClientWindow(hWin), 1);
				vTaskSuspend(NULL);
			}
		}
		if(xSemaphoreTake(xMaWinFlag_ON,0) == pdPASS)
		{
			if(MWin_Flag ==0)
			{
				MWin_Flag = 1;
				hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
			}
		}
		if(uxQueueMessagesWaiting( xCAN_V_TxMessage ) != 0)
		{
			xQueueReceive( xCAN_V_TxMessage, &MV_Data, 0);
			printf("QueRecie CANTrans.\r\n");
			WM_InvalidateWindow(hWin);
		}
		if(uxQueueMessagesWaiting( xTIM_T_TxMessage ) != 0)
		{
			xQueueReceive( xTIM_T_TxMessage, &MT_Data, 0);
			WM_InvalidateWindow(hWin);
		}
		GUI_Delay(1);
	}
}
