/*********************************************************************
*
*       CreateRecord_Window
*/
void Record_Window(void *pvParameters);
void Record_Window(void *pvParameters)
{
  WM_HWIN hWin,hItem;
	uint8_t RecWin_Flag=0;
	
	WM_SetCreateFlags(WM_CF_MEMDEV);  						/* Use memory devices on all windows to avoid flicker */
	WM_SetDesktopColor(GUI_BLACK);      /* Automacally update desktop window */

	Init_VMsg(&SV_Data);
	Init_TMsg(&ST_Data);
	
  while(1)
	{	
		if(xSemaphoreTake(xRecWinFlag_OFF,0) == pdPASS)
		{
			if(RecWin_Flag ==1)
			{
				Init_TMsg(&ST_Data);
				RecWin_Flag = 0;
				Rec_Receive=0;
				GUI_EndDialog(WM_GetClientWindow(hWin), 0);
				vTaskSuspend(NULL);
			}
		}
		GUI_Delay(1);
		if(xSemaphoreTake(xRecWinFlag_ON,0) == pdPASS)
		{
			if(RecWin_Flag ==0)
			{
				xSemaphoreGive(xAskRecord);
				hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
				RecWin_Flag = 1;
			}
		}
		if(uxQueueMessagesWaiting( xTIM_T_TxMessage ) != 0)
		{
			xQueueReceive( xTIM_T_TxMessage, &ST_Data, 0);
			WM_InvalidateWindow(hWin);
		}
		GUI_Delay(1);
		if(uxQueueMessagesWaiting( xCAN_V_RecordMessage ) != 0)
		{
			while(uxQueueMessagesWaiting( xCAN_V_RecordMessage ) != 0)
			{
				xQueueReceive( xCAN_V_RecordMessage, &Rec_Data[Rec_Receive], 0);
				Rec_Receive++;
			}
			for(k=0;k<Rec_Receive;)
			{	
				Text[0]=OrdNumConvert(k+1);
				Text[1]=NumConvert(Rec_Data[k].V_Max);
				Text[2]=NumConvert(Rec_Data[k].V_Min);
				
				if(k!=0){
					VminTime=Rec_Data[k-1].VminTime;
					VmaxTime=Rec_Data[k].VmaxTime;
					Text[3]=NumConvert(VmaxTime-VminTime);
				}
			  else
					Text[3]=NumConvert(VmaxTime);
				VmaxTime=(Rec_Data[k].VmaxTime);
				VminTime=(Rec_Data[k].VminTime);
				Text[4]=NumConvert(VminTime-VmaxTime);
				
				hItem = WM_GetDialogItem(hWin, ID_LISTVIEW_0);
				LISTVIEW_AddRow(hItem, Text);											//Ìí¼Ó1ÐÐ
				k++;
			}
		}
		GUI_Delay(1);
	}
}
